import React from 'react'
import { useEffect, useState } from 'react'
import { FaLongArrowAltDown, FaLongArrowAltUp } from 'react-icons/fa';

const Widget = () => {
    const [data,setData] = useState([])
    useEffect(()=>{
        fetch('https://api.coinlore.net/api/tickers/?start=100&limit=5')
        .then((res)=>res.json())
        .then((response)=>{
            setData(response.data)
        })
    })
    return(
        <div>
{data.map((datum)=>{
            return (
                <div>
                    <h6> {datum.symbol}/NGN</h6>
                    <p>{datum.percent_change_24h}%{datum.percent_change_24h < 0? <FaLongArrowAltDown style={{color:'red'}}/> : <FaLongArrowAltUp style={{color:'green'}}/> } </p>
                    <p>{datum.price_btc} </p>
                    <p>{Math.ceil(datum.price_usd)*700}NGN</p>
                </div>
              )
        })}
        </div>

    )


}

export default Widget
