import React from "react";
import { Link } from "react-router-dom";

const initialIndividual ={
  username:'',
  fullname:'',
  email:'',
  phonenum:'',
  usernameError:'',
  fullnameError:'',
  emailError:'',
  phonenumError:''

};
export default class Individual extends React.Component{
  state = initialIndividual;

  handleChange = event=>{
    const ischeckBox = event.target.type === 'individualbox';
    this.setState({
      [event.target.name]: ischeckBox
      ? event.target.checked
      : event.target.value
    });
  };

  validate = ()=> {
    let usernameError='';
    let fullnameError='';
    let emailError='';
    let phonenumError=''

    if (!this.state.username) {
      usernameError = "name cannot be blank";
    }
    if (!this.state.phonenum) {
      phonenumError = "phone number cannot be blank";
    }
    if (!this.state.fullname) {
      fullnameError = "Full name cannot be blank";
    }

    if (!this.state.email.includes("@")) {
      emailError = "invalid email";

  }

  if (usernameError || fullnameError || emailError || phonenumError) {
    this.setState({usernameError, fullnameError,emailError,phonenumError})
    return false;
  } 
    return true;


};
  handleClick = event =>{
    event.preventDefault();
    const isSubmit = this.validate();
    if (isSubmit)
        {this.setState(initialIndividual)

    }
  };

  render(){
    return (
      <form onSubmit={this.handleSubmit}>
 <div className="individual-hero">
        <h2>Welcome!</h2>
        <button className='individual-btn1'> <Link to = '/Individual'> Individual </Link></button>
          <button className='individual-btn1'> <Link to = '/Business'>Business </Link> </button>
          <div>
          <input type="username" 
          placeholder='Username'
          value={this.state.username}
          onChange={this.handleClick}
          />
          <div style={{ fontSize: 12, color: "red" }}>{this.state.usernameError}</div>
          </div>

          <div>
          <input type="fullname" 
          placeholder='Full Name'
          value={this.state.fullname}
          onChange={this.handleClick}
          />
          <div style={{ fontSize: 12, color: "red" }}>{this.state.fullnameError}</div>
          </div>

          <div>
          <input type="email" 
          placeholder='Email'
          value={this.state.email}
          onChange={this.handleClick}
          />
          <div style={{ fontSize: 12, color: "red" }}>{this.state.emailError}</div>
          </div>

          <div>
          <input type="phonenum" 
          placeholder='P'
          value={this.state.username}
          onChange={this.handleClick}
          />
          <div style={{ fontSize: 12, color: "red" }}>{this.state.usernameError}</div>
          </div>


          <input type="text" placeholder='Full Name' />
          <input type="text" placeholder='Email' />
          <input type="text" placeholder='Phone Number' />
          <input type="text" placeholder='Referral Code(optional)' />
      </div>
      </form>

    )
  }
}



